mapper.select:
select
id, box_code, label_code, box_name, area, business_bureau, camp_service, job_number, img_url, status,
exception_type, exception_info, create_time
from obd-scan.pc_obd_box


        and id = #{id}


        and box_code = #{boxCode}


        and label_code = #{labelCode}


        and box_name = #{boxName}


        and area = #{area}


        and business_bureau = #{businessBureau}


        and camp_service = #{campService}


        and job_number = #{jobNumber}


        and img_url = #{imgUrl}


        and status = #{status}


        and exception_type = #{exceptionType}


        and exception_info = #{exceptionInfo}


        and create_time = #{createTime}
